import { Component, OnInit } from '@angular/core';
import { take } from 'rxjs';
import { MyDataService } from '../services/my-data.service';
import { IArticle } from '../module/article';
import ArticlesData from '../assets/data/articles.json';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {
  public myData$: any = [];
  public articles: IArticle[] = ArticlesData;
  // public myData$ = [];

  constructor(private myDataService: MyDataService) {}

  ngOnInit(): void {
    this.myData$ = this.myDataService.getArticles();
    // this.myDataService
    //   .getArticles()
    //   // .pipe(take(1))
    //   .subscribe((data) => {
    //     this.myData$ = data;
    //   });
    // this.myData$ = this.myDataService.getArticles();
    // console.log(JSON.stringify(this.myDataService.getArticles()));
  }
}
<app-navbar></app-navbar>
<main>
  <div *ngFor="let article of myData$ | async">
    <h3>{{ article.title }}</h3>
    <p>{{ article.content }}</p>
  </div>
  <!-- {{ myData | json }} -->
</main>
