import { Component, OnInit } from '@angular/core';
import { take } from 'rxjs';
import { MyDataService } from '../services/my-data.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {
  myData: any;
  myData$: any;
  constructor(private myDataService: MyDataService) {}

  ngOnInit(): void {
    // this.myDataService
    //   .getProducts()
    //   .pipe(take(1))
    //   .subscribe((data) => {
    //     this.myData = data;
    //   });
    this.myData$ = this.myDataService.getProducts();
  }
}
<main>
  <h2>TESDT</h2>
  {{ myData | json }}
  <div ngForOf="let item of myData$ | async">
    <h3>{{ item.sku }}</h3>
    <p>{{ item.name }}</p>
    <p>{{ item.price }}</p>
  </div>
</main>
